mapper.update:
UPDATE ANIMAL_DETAILS
SET ADOPTION_STATUS = #{status}
WHERE ANIMAL_NO = #{animalNo}

UPDATE ADOPTION_POSTS
SET VIEWS = VIEWS+1
WHERE ANIMAL_NO = #{animalNo}

mapper.select:
SELECT COUNT(*)
FROM ADOPTION_POSTS
WHERE ANIMAL_NO = #{anino}

SELECT COUNT(*)
FROM ANIMAL_DETAILS
WHERE USER_ID = #{userId}

SELECT COUNT(*)
FROM ADOPTION_APPLICATIONS
WHERE USER_ID = #{userId}

mapper.update:
UPDATE ANIMAL_DETAILS
SET ADOPTION_STATUS ='반려'
WHERE ANIMAL_NO = #{anino}

mapper.select:
SELECT COUNT(*)
FROM ADOPTION_APPLICATIONS
WHERE ANIMAL_NO = #{animalNo}
AND USER_ID = #{userId}
